mapper.select:
SELECT e.* FROM event e JOIN member m ON e.memId = m.memId WHERE m.gymName IN
#{name}
ORDER BY eventDate DESC, m.gymName LIMIT #{limit} OFFSET #{offset}

SELECT e.*
FROM event e
WHERE memId = #{memId}
ORDER BY eventDate DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM event
WHERE memId = #{memId}

mapper.update:
UPDATE event
SET eventTitle = #{eventTitle}, eventDeadLine = #{eventDeadLine},
    eventImg = #{eventImg}, eventTitleContents = #{eventTitleContents},
    eventContents = #{eventContents}, eventPrice = #{eventPrice}
WHERE eventNo = #{eventNo}

mapper.select:
SELECT g.* FROM gallery g JOIN member m ON m.memId = g.memId WHERE m.gymName IN
#{name}
ORDER BY galleryDate DESC, m.gymName LIMIT #{limit} OFFSET #{offset}

SELECT *
FROM gallery		
WHERE memId = #{memId}
ORDER BY galleryDate DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM gallery
WHERE memId = #{memId}

mapper.update:
UPDATE gallery
SET galleryTitle = #{galleryTitle}, galleryImg = #{galleryImg},
    galleryDate = #{galleryDate}, galleryContents = #{galleryContents}	    
WHERE galleryNo = #{galleryNo}
